CREATE TABLE TB_SCRP_CLIENTE (IDENT INTEGER DEFAULT IDENTITY GENERATED BY DEFAULT NOT NULL, ID_PESSOA INTEGER NOT NULL, EMAIL1_CLI VARCHAR(256), EMAIL2_CLI VARCHAR(256), DDD_TEL CHAR(3), TELEFONE CHAR(9), CEP CHAR(8), LOGRADOURO VARCHAR(255), NUMERO VARCHAR(10), COMPLEMENTO VARCHAR(100), MUNICIPIO VARCHAR(80), UF CHAR(2), COD_MUNICIPIO_IBGE VARCHAR(7), CEP_COBR CHAR(8), LOGRADOURO_COBRANCA VARCHAR(255), NUMERO_COBR VARCHAR(10), COMPLEMENTO_COBR VARCHAR(100), MUNICIPIO_COBRANCA VARCHAR(80), UF_COBRANCA CHAR(2), BAIRRO VARCHAR(100), BAIRRO_COBRANCA VARCHAR(100), COD_MUNICIPIO_IBGE_COBRANCA VARCHAR(7), DATA_INCLUSAO TIMESTAMP, DATA_EXCLUSAO TIMESTAMP, DATA_ALTERACAO TIMESTAMP, ID_STATUS INTEGER, ID_USUAR_INCLUSAO INTEGER, ID_USUAR_EXCLUSAO INTEGER, ID_USUAR_ALTERACAO INTEGER, NOME_COBR VARCHAR(255), EMAIL1_COBR VARCHAR(255), EMAIL2_COBR VARCHAR(255), STATUS_BLOQUEADO SMALLINT, UNIDADE VARCHAR(14), DATA_ATUALIZACAO TIMESTAMP, PRIMARY KEY (IDENT), CONSTRAINT CFAME003_001_002 FOREIGN KEY (ID_USUAR_INCLUSAO) REFERENCES "AME_ACS"."TB_SCRP_USUARIO" ("IDENT") ON UPDATE NO ACTION ON DELETE NO ACTION, CONSTRAINT CFAME003_001_003 FOREIGN KEY (ID_USUAR_EXCLUSAO) REFERENCES "AME_ACS"."TB_SCRP_USUARIO" ("IDENT") ON UPDATE NO ACTION ON DELETE NO ACTION, CONSTRAINT CFAME003_002_001 FOREIGN KEY (ID_PESSOA) REFERENCES "TB_SCRP_PESSOA" ("IDENT") ON UPDATE NO ACTION ON DELETE NO ACTION);
CREATE TABLE TB_SCRP_CLIENTE_HAS_CLASSE_CLIENTE (ID_CLIENTE INTEGER NOT NULL, ID_CLASSE_CLIENTE INTEGER NOT NULL, PRIMARY KEY (ID_CLIENTE, ID_CLASSE_CLIENTE), CONSTRAINT CFAME017_013_002 FOREIGN KEY (ID_CLASSE_CLIENTE) REFERENCES "TB_SCRP_CLASSE_CLIENTE" ("IDENT") ON UPDATE NO ACTION ON DELETE NO ACTION, CONSTRAINT CFAME017_003_001 FOREIGN KEY (ID_CLIENTE) REFERENCES "TB_SCRP_CLIENTE" ("IDENT") ON UPDATE NO ACTION ON DELETE NO ACTION);
CREATE TABLE TB_SCRP_CONTRATO (IDENT INTEGER DEFAULT IDENTITY GENERATED BY DEFAULT NOT NULL, NUM_PROPOSTA VARCHAR(16), NUM_CONTRATO VARCHAR(15), DATA_EMISSAO TIMESTAMP, DATA_ASSINATURA TIMESTAMP, ID_STATUS INTEGER, ID_CLIENTE INTEGER, ID_MODELO_CONTRATO INTEGER, ID_USUAR_ALTERACAO INTEGER, ID_USUAR_INCLUSAO INTEGER, ID_USUAR_EXCLUSAO INTEGER, DATA_ALTERACAO TIMESTAMP, DATA_EXCLUSAO TIMESTAMP, DATA_INCLUSAO TIMESTAMP, FLAG_VIEW_CLIENTE CHAR(1) DEFAULT 'S', PRIMARY KEY (IDENT), CONSTRAINT CFAME027_003_001 FOREIGN KEY (ID_CLIENTE) REFERENCES "TB_SCRP_CLIENTE" ("IDENT") ON UPDATE NO ACTION ON DELETE NO ACTION, CONSTRAINT CFAME027_015_003 FOREIGN KEY (ID_MODELO_CONTRATO) REFERENCES "TB_SCRP_MODELO_CONTRATO" ("IDENT") ON UPDATE NO ACTION ON DELETE RESTRICT, CONSTRAINT CFAME027_010_001 FOREIGN KEY (ID_STATUS) REFERENCES "TB_SCRP_STATUS" ("IDENT") ON UPDATE NO ACTION ON DELETE RESTRICT);
CREATE TABLE TB_SCRP_DOCUMENTOS_CLIENTE (IDENT INTEGER DEFAULT IDENTITY GENERATED BY DEFAULT NOT NULL, ID_CLIENTE INTEGER, ID_TIPO_DOCUMENTO INTEGER, ID_STATUS INTEGER, DATA_VALIDADE DATE, DOCUMENTO BLOB, OBSERVACAO VARCHAR(256), TIPO_ARQUIVO VARCHAR(20), NUM_DOCUMENTO VARCHAR(30), CLASSE_EMPRESA VARCHAR(10), COD_ATIV_ECONOMICA VARCHAR(10), DB2_GENERATED_ROWID_FOR_LOBS ROWID DEFAULT ROWID GENERATED ALWAYS NOT NULL, DATA_INCLUSAO TIMESTAMP, DATA_EXCLUSAO TIMESTAMP, DATA_ALTERACAO TIMESTAMP, ID_USUAR_EXCLUSAO INTEGER, ID_USUAR_INCLUSAO INTEGER, ID_USUAR_ALTERACAO INTEGER, PRIMARY KEY (IDENT), CONSTRAINT CFAME028_003_001 FOREIGN KEY (ID_CLIENTE) REFERENCES "TB_SCRP_CLIENTE" ("IDENT") ON UPDATE NO ACTION ON DELETE NO ACTION, CONSTRAINT CFAME028_010_003 FOREIGN KEY (ID_STATUS) REFERENCES "TB_SCRP_STATUS" ("IDENT") ON UPDATE NO ACTION ON DELETE NO ACTION);
CREATE TABLE TB_SCRP_PESSOA (IDENT INTEGER DEFAULT IDENTITY GENERATED BY DEFAULT NOT NULL, TP_PESSOA CHAR(1) NOT NULL, CPF_PESSOA VARCHAR(11), NOME_PESSOA VARCHAR(200), CNPJ_PESSOA VARCHAR(14), RAZAO_SOCIAL VARCHAR(200), DATA_INCLUSAO TIMESTAMP, DATA_EXCLUSAO TIMESTAMP, ID_STATUS INTEGER, ID_USUAR_INCLUSAO INTEGER, ID_USUAR_EXCLUSAO INTEGER, ID_USUAR_ALTERACAO INTEGER, DATA_ALTERACAO TIMESTAMP, UNIDADE VARCHAR(14), PRIMARY KEY (IDENT), CONSTRAINT CFAME002_001_003 FOREIGN KEY (ID_USUAR_EXCLUSAO) REFERENCES "AME_ACS"."TB_SCRP_USUARIO" ("IDENT") ON UPDATE NO ACTION ON DELETE NO ACTION, CONSTRAINT CFAME002_001_002 FOREIGN KEY (ID_USUAR_INCLUSAO) REFERENCES "AME_ACS"."TB_SCRP_USUARIO" ("IDENT") ON UPDATE NO ACTION ON DELETE NO ACTION, CONSTRAINT CFAME002_010_001 FOREIGN KEY (ID_STATUS) REFERENCES "TB_SCRP_STATUS" ("IDENT") ON UPDATE NO ACTION ON DELETE NO ACTION);

